<div class="contributors-wrapper">    <div class="contributors-container">        <div class="contributors-header"><?php echo $contrib_option['contrib_title']; ?></div>            <div class="contributors-content">                <ul>                 <?php                    //get our order from our stored input value and convert to array                    $orderby    = explode(',', $contrib_option['contrib_order']);//                    $orderby[$i] = isset($orderby[$i]) ? $orderby[$i] : null;                    $args       = array(                        'fields' => 'all_with_meta'                    );                    // The Query                    $user_query = new WP_User_Query($args);                    // User Loop                    if (!empty($user_query->results))                      {                        for ($i = 0; $i <= count($orderby); $i++)                          {                            foreach ($user_query->results as $author)                              {                                if(isset($orderby[$i])) {                                        if ($orderby[$i] == $author->ID) {                                            // get all the user's data                                            $author_info = get_userdata($author->ID);                                            $default_fullname = $author_info->first_name . ' ' . $author_info->last_name;                                            $img = get_user_meta($author->ID, 'add_image', true);                                            $author_posts_linkhref = get_author_posts_url( $author->ID, $author->user_nicename );                                            echo '<li class="begin"><a title="' . $default_fullname . '" href="' . $author_posts_linkhref . '">' . '<img class="contrib-thumb" src="' . $img . '">' . '</a>';                                            $contrib_name = get_user_meta($author->ID, 'contrib_name', true);                                            if ($contrib_name) {                                                echo '<a href="' . $author_posts_linkhref . '"class="contributor-name">' . $contrib_name . '</a>';                                            } else {                                                echo '<a href="' . $author_posts_linkhref . '"class="contributor-name">' . $default_fullname . '</a>';                                            }                                            $role = get_user_meta($author->ID, 'contrib_role', true);                                            if ($role) {                                                echo '<a href="' . $author_posts_linkhref . '"class="contributor-role">' . $role . '</a>';                                            } else {                                                echo '<a href="' . $author_posts_linkhref . '"class="contributor-role">' . $author_info->roles[0] . '</a>';                                            }                                            echo '</li>';                                        }                                    }                                }                            }                        ?>                <?php                echo '</ul>';              }            else              {                echo 'No authors found';              }            ?>                      <div class="clear"></div></div></div></div>